import React, { Component } from 'react';

export default class DeleteSongModal extends Component {

    render() {
        const {songName, deleteSongConfirm, deleteSongCancel} = this.props;
        //const { store } = useContext(GlobalStoreContext);



        return (
            <div 
                class="modal" 
                id="delete-song-modal" 
                data-animation="slideInOutLeft">
                    <div class="modal-root" id='verify-delete-song-root'>
                        <div class="modal-north">
                            Remove song?
                        </div>
                        <div class="modal-center">
                            <div class="modal-center-content">
                                Are you sure you wish to permanently remove {songName} from the playlist?
                            </div>
                        </div>
                        <div class="modal-south">
                            <input type="button" 
                                id="delete-song-confirm-button" 
                                class="modal-button" 
                                onClick={store.addDeleteSongTransaction}
                                value='Confirm' />
                            <input type="button" 
                                id="delete-song-cancel-button" 
                                class="modal-button" 
                                onClick={store.hideDeleteSongModal}
                                value='Cancel' />
                        </div>
                    </div>
            </div>
        );
    }
}
